import React, {Component} from 'react';

class RouteProject extends Component {

    render(){
      return (
        <div class="main">
        <h3>All projects</h3>
        <div class="card project">
          <img class="card-img-top" src="project.jpg" alt="Card image cap" />
          <div className="card-body">
	          <h5 className="card-title">{name}</h5>
	          <p className="card-text">{description}</p>
	          <p>
	            <i className="fas fa-heart"></i>
							{ ((user != null) && (user.id == user_id )) ? (
							<>
							<i onClick={this.handleEditClick} className="fas fa-edit"></i>
	            <i onClick={this.handleTrashClick} className="fas fa-trash"></i>)
							</>
							) : null}


	          </p>
	        </div>
        </div>
      </div>
      )
    }
}

export default RouteProject;
