import React, {Component} from 'react';

class RouteProject extends Component {
    constructor(props){
        super(props)

        this.state = {
          activeView:'projects',
          projects:[
            {
              id:1,
              name:'Build a hut',
              description: 'Nice project'
            },{
              id:2,
              name:'Make a basket',
              description: 'Pretty project'
            }
          ]
        };
    }

	handleTrashClick = () => {
		var {deleteProjects,id,} = this.props;

		deleteProjects(id);
	}
	handleEditClick = () => {
		var {setActiveView, setProjectToUpdate, id,} = this.props;

		setProjectToUpdate(id)
		setActiveView('edit-project')

	}

  	render(){

	  	var {name,description} = this.props
      return (
        <div className="main">
        <h3>All projects</h3>
        <div className="card project">
          <img className="card-img-top" src="project.jpg" alt="Card image cap" />
          <div className="card-body">
            <h5 className="card-title">Card title</h5>
            <p className="card-text">Some quick example text to build on the card title</p>
            <p>
              <i className="fas fa-heart"></i>
              <i className="fas fa-edit"></i>
              <i className="fas fa-trash"></i>
            </p>
          </div>
        </div>
      </div>
      )
    }
}

export default RouteProject;
