import React, {Component} from 'react';

class RouteProject extends Component {
    constructor(props){
        super(props)

        this.state = {
          activeView:'projects',
          projects:[
            {
              id:1,
              name:'Build a hut',
              description: 'Nice project'
            },{
              id:2,
              name:'Make a basket',
              description: 'Pretty project'
            }
          ]
        };
    }

	handleTrashClick = () => {
		var {deleteProjects,id,} = this.props;

		deleteProjects(id);
	}
	handleEditClick = () => {
		var {setActiveView, setProjectToUpdate, id,} = this.props;

		setProjectToUpdate(id)
		setActiveView('edit-project')

	}

  	render(){

	  	var {name,description} = this.props
      return (
        <div className="main">
        <h3>All projects</h3>
        {
                projects.map((project) => {

                  var projectProps = {
                    ...project,
                    key: project.id,
                    deleteProjects: this.deleteProjects,
                    setActiveView: this.setActiveView,
                    setProjectToUpdate: this.setProjectToUpdate,
                    user:this.state.currentUser
                  };
                  return (<Project {...projectProps} />)
                })
              }
      </div>
      )
    }
}

export default RouteProject;
